function findProduct(t){return products[findProductKey(t)]}function findProductKey(t){for(var r=0;r<products.length;r++)if(products[r].id==t)return r}var products=[{id:1,name:"Angular",description:"Superheroic JavaScript MVW Framework.",price:100},{id:2,name:"Ember",description:"A framework for creating ambitious web applications.",price:100},{id:3,name:"React",description:"A JavaScript Library for building user interfaces.",price:100}],List=Vue.extend({template:"#product-list",data:function(){return{products:products,searchKey:""}},computed:{filteredProducts:function(){return this.products.filter(function(t){return""==this.searchKey||-1!==t.name.indexOf(this.searchKey)},this)}}}),Product=Vue.extend({template:"#product",data:function(){return{product:findProduct(this.$route.params.product_id)}}}),ProductEdit=Vue.extend({template:"#product-edit",data:function(){return{product:findProduct(this.$route.params.product_id)}},methods:{updateProduct:function(){var t=this.product;products[findProductKey(t.id)]={id:t.id,name:t.name,description:t.description,price:t.price},router.push("/")}}}),ProductDelete=Vue.extend({template:"#product-delete",data:function(){return{product:findProduct(this.$route.params.product_id)}},methods:{deleteProduct:function(){products.splice(findProductKey(this.$route.params.product_id),1),router.push("/")}}}),AddProduct=Vue.extend({template:"#add-product",data:function(){return{product:{name:"",description:"",price:""}}},methods:{createProduct:function(){var t=this.product;products.push({id:Math.random().toString().split(".")[1],name:t.name,description:t.description,price:t.price}),router.push("/")}}}),router=new VueRouter({routes:[{path:"/",component:List},{path:"/product/:product_id",component:Product,name:"product"},{path:"/add-product",component:AddProduct},{path:"/product/:product_id/edit",component:ProductEdit,name:"product-edit"},{path:"/product/:product_id/delete",component:ProductDelete,name:"product-delete"}]});app=new Vue({router:router}).$mount("#app");